<?php if (!defined('THINK_PATH')) exit();?><div class="top_bar"><ul id="banner_list" class="banner"><?php if(is_array($ad_list)): $i = 0; $__LIST__ = $ad_list;if( count($__LIST__)==0 ) : echo "" ;else: foreach($__LIST__ as $key=>$ad): $mod = ($i % 2 );++$i;?><li style="display:none;z-index:1; opacity:1;background:url(<?php echo attach($ad['content'],'banner');?>) no-repeat center center;" ><a target="_blank" href="<?php echo ($ad["url"]); ?>" class="pic" title="<?php echo ($ad["name"]); ?>"></a></li><?php endforeach; endif; else: echo "" ;endif; ?></ul></div>